import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const Student = () => {
  const [students, setStudents] = useState([]);
  const [filteredStudents, setFilteredStudents] = useState([]);
  const [selectedFaculty, setSelectedFaculty] = useState('All');

  useEffect(() => {
    fetch('/student')
      .then((response) => response.json())
      .then((data) => {
        setStudents(data);
        setFilteredStudents(data);
      });
  }, []);

  const handleFacultyFilter = (event) => {
    const faculty = event.target.value;
    setSelectedFaculty(faculty);

    if (faculty === 'All') {
      setFilteredStudents(students);
    } else {
      const filtered = students.filter((student) => student.faculty === faculty);
      setFilteredStudents(filtered);
    }
  };

  const handleDelete = (id) => {
    fetch(`/student/${id}`, {
      method: 'DELETE',
    }).then(() => {
      setStudents((prevStudents) => prevStudents.filter((student) => student.id !== id));
      setFilteredStudents((prevFiltered) => prevFiltered.filter((student) => student.id !== id));
    });
  };

  return (
    <>
      <h2>Student List</h2>
      <select value={selectedFaculty} onChange={handleFacultyFilter} data-testid="filter">
        <option value="All">All</option>
        <option value="Fakultas Ekonomi">Fakultas Ekonomi</option>
        <option value="Fakultas Ilmu Sosial dan Politik">Fakultas Ilmu Sosial dan Politik</option>
        <option value="Fakultas Teknik">Fakultas Teknik</option>
        <option value="Fakultas Teknologi Informasi dan Sains">Fakultas Teknologi Informasi dan Sains</option>
      </select>

      {students.length === 0 ? (
        <p>Loading...</p>
      ) : (
        <table id="table-student">
          <thead>
            <tr>
              <th>No</th>
              <th>Full Name</th>
              <th>Faculty</th>
              <th>Program Study</th>
              <th>Option</th>
            </tr>
          </thead>
          <tbody>
            {filteredStudents.map((student, index) => (
              <tr key={student.id} className="student-data-row">
                <td>{index + 1}</td>
                <td>
                  <Link to={`/student/${student.id}`}>{student.fullName}</Link>
                </td>
                <td>{student.faculty}</td>
                <td>{student.programStudy}</td>
                <td>
                  <button
                    onClick={() => handleDelete(student.id)}
                    data-testid={`delete-${student.id}`}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </>
  );
};

export default Student;
